        }

	public function Services_JSON_Error($message = 'unknown error', $code = null,
                                     $mode = null, $options = null, $userinfo = null) {
		self::__construct($message = 'unknown error', $code = null,
                                     $mode = null, $options = null, $userinfo = null);
	}
    }

